// import logo from './images/revs.png';
import "./customerWindowGUI.css";
import Cart from "./Cart";
import Menu from "./Menu";
import data from "./tempData";
import Header from "./Header";
import React, { Fragment, useEffect, useState } from "react";
import DeliveryInput from "./deliveryAddress";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

function Customer() {
  const getMenuitems = async () => {
    try {
      // const response = await fetch ("http://localhost:3000/menuitems_list");
      // const response = await fetch ("http://localhost:3000/inventory");

      // const jsonData = await response.json();

      // console.log(jsonData);

      var responseClone; // 1
      fetch("https://example.com/some/path/to/json")
        .then(function (response) {
          responseClone = response.clone(); // 2
          return response.json();
        })
        .then(
          function (rejectionReason) {
            // 3
            console.log(
              "COOOLIO BBBBBBBBBBBBBBBBB:",
              rejectionReason,
              responseClone
            ); // 4
            responseClone
              .text() // 5
              .then(function (bodyText) {
                console.log(
                  "Received the following instead of valid JSON:",
                  bodyText
                ); // 6
              });
          }
        );
    } catch (err) {
      console.error("i will kms fr:  see error message below");
      console.error(err.message);
    }
  };

  useEffect(() => {
    getMenuitems();
  });

  const { items } = data;
  return (
    // <div className="Customer Customer-header">
    <Container col>
      <Header></Header>
      {/* <div className="row"> */}
      <Row>
        <Menu items={items}></Menu>
        {/* <div className="Customer Customer-header"> */}
        <Col>
          <Cart></Cart>
          <DeliveryInput></DeliveryInput>
        </Col>
        {/* </div> */}

        {/* </div> */}
      </Row>
    </Container>
  );
}

export default Customer;
