// import logo from './images/revs.png';
import './customerWindowGUI.css';
import Cart from './Cart';
import Menu from './Menu';
import data from './tempData';
import Header from './Header';
import React, {Fragment, useEffect, useState} from "react";
import DeliveryInput from './deliveryAddress';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';


function Customer() {
  const getMenuitems = async () => {
    try{
      const response = await fetch ("https//localhost:3000/menuitems_list");
      const jsonData = await response.json();
      console.log(jsonData);
    }catch (err){
      console.error(err.message);
      console.error(response.json());

    }
  }

  useEffect(()=> {
    getMenuitems();
  })

  const {items} = data;
  return ( 
    // <div className="Customer Customer-header">
    <Container col>
        <Header></Header>
        {/* <div className="row"> */}
        <Row>
          <Menu items={items}></Menu>
          {/* <div className="Customer Customer-header"> */}
          <Col>
            <Cart></Cart>
            <DeliveryInput></DeliveryInput>
          </Col>  
          {/* </div> */}
        
          {/* </div> */}
        </Row>
    </Container>

  );
}

export default Customer;
